import { pool } from '../db';

const bcrypt = require('bcryptjs');

export const signin = async (req, res) => {


}

export const signup = async (req, res) => {

    const {paterno, materno, nombre, direccion, nombre_usuario,contrasena} = req.body

    const salt = bcrypt.genSalt(10);
    const encryptPassword = bcrypt.hashSync(contrasena,salt);


    await pool.query('INSERT INTO Personal (paterno, materno, nombre, direccion, nombre_usuario) VALUES (?, ?, ?, ?, ?, NOW(), ?, ?)', [paterno, materno, nombre, direccion, nombre_usuario, nombre_usuario, encryptPassword])

}

