sourcesContent:
import { pool } from '../db';

const bcrypt = require('bcryptjs');

export const signin = async (req, res) => {

    const { nombre_usuario, contrasena } = req.body

    const user = await pool.query('SELECT * FROM Personal WHERE nombre_usuario = ?', [nombre_usuario])

    if (user.length === 0) {
        return res.status(401).json({ message: 'Usuario no encontrado' })
    }

    const match = await bcrypt.compare(contrasena, user[0].contrasena);

    if (!match) {
        return res.status(401).json({ message: 'Contraseña incorrecta' })
    }

    res.json({ message: 'Usuario logeado' })


}

export const signup = async (req, res) => {

    const {paterno, materno, nombre, direccion, nombre_usuario,contrasena} = req.body

    const salt = bcrypt.genSalt(10);
    const encryptPassword = bcrypt.hashSync(contrasena,salt);


    await pool.query('INSERT INTO Personal (apaterno, amaterno, nombre, direccion, fechadeingreso, nombre_usuario, contrasena) VALUES (?, ?, ?, ?, ?, NOW(), ?, ?)', [paterno, materno, nombre, direccion, nombre_usuario, nombre_usuario, encryptPassword])

    res.json({ message: 'Usuario registrado' })
}

